// Botpress Service Integration
export interface BotpressMessage {
  id: string;
  type: 'text' | 'image' | 'file' | 'card' | 'carousel' | 'quick_reply';
  text?: string;
  data?: any;
  timestamp: Date;
}

export interface BotpressConfig {
  botId: string;
  userId: string;
  conversationId?: string;
  apiUrl?: string;
}

export class BotpressService {
  private config: BotpressConfig;
  private conversationId: string | null = null;
  private isConnected = false;

  constructor(config: BotpressConfig) {
    this.config = config;
    this.conversationId = config.conversationId || null;
  }

  // Initialize connection to Botpress
  async initialize(): Promise<void> {
    try {
      // Generate a unique conversation ID if not provided
      if (!this.conversationId) {
        this.conversationId = `conv_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      }

      // Test connection by sending a ping
      await this.sendMessage('ping');
      this.isConnected = true;
      console.log('Botpress service initialized successfully');
    } catch (error) {
      console.error('Failed to initialize Botpress service:', error);
      // Don't throw error, just mark as not connected
      this.isConnected = false;
    }
  }

  // Send message to Botpress and get response
  async sendMessage(message: string): Promise<BotpressMessage[]> {
    try {
      // For now, we'll use a mock response since the Botpress API requires specific configuration
      // In a real implementation, you would use the actual Botpress API
      console.log('Sending message to Botpress:', message);

      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Return a mock response for now
      return this.getMockResponse(message);
    } catch (error) {
      console.error('Error sending message to Botpress:', error);
      // Fallback to local AI response
      return this.getFallbackResponse(message);
    }
  }

  // Mock response for testing
  private getMockResponse(message: string): BotpressMessage[] {
    const responses = [
      "I understand you're going through a difficult time. I'm here to listen and support you.",
      "Thank you for sharing that with me. It takes courage to open up about your feelings.",
      "I can hear that you're feeling overwhelmed. Let's work through this together, one step at a time.",
      "Your feelings are completely valid. Sometimes just talking about what's on your mind can help.",
      "I'm glad you reached out. You're not alone in this, and I'm here to help you through it.",
      "That sounds really challenging. I want you to know that it's okay to not be okay sometimes.",
      "I appreciate you trusting me with your thoughts. Let's explore some strategies that might help you feel better.",
      "I can sense the strength in your words, even when things feel difficult. You're doing better than you might think."
    ];

    const randomResponse = responses[Math.floor(Math.random() * responses.length)];

    return [{
      id: `botpress_${Date.now()}`,
      type: 'text',
      text: randomResponse,
      timestamp: new Date()
    }];
  }

  // Parse Botpress response format
  private parseBotpressResponse(data: any): BotpressMessage[] {
    const messages: BotpressMessage[] = [];

    if (data.messages && Array.isArray(data.messages)) {
      data.messages.forEach((msg: any, index: number) => {
        messages.push({
          id: msg.id || `msg_${Date.now()}_${index}`,
          type: msg.type || 'text',
          text: msg.text || msg.message || '',
          data: msg.data,
          timestamp: new Date()
        });
      });
    } else if (data.text) {
      messages.push({
        id: `msg_${Date.now()}`,
        type: 'text',
        text: data.text,
        timestamp: new Date()
      });
    }

    return messages;
  }

  // Fallback response when Botpress is unavailable
  private getFallbackResponse(message: string): BotpressMessage[] {
    const responses = [
      "I'm here to listen and support you. How are you feeling today?",
      "Thank you for sharing that with me. I understand this might be difficult to talk about.",
      "I'm glad you reached out. Let's work through this together, one step at a time.",
      "Your feelings are valid, and it's okay to not be okay sometimes. I'm here to help.",
      "I can hear that you're going through a challenging time. You're not alone in this."
    ];

    const randomResponse = responses[Math.floor(Math.random() * responses.length)];

    return [{
      id: `fallback_${Date.now()}`,
      type: 'text',
      text: randomResponse,
      timestamp: new Date()
    }];
  }

  // Get initial greeting message
  async getInitialGreeting(): Promise<BotpressMessage[]> {
    try {
      // Try to get a personalized greeting from Botpress
      const response = await this.sendMessage('start conversation');

      // If no response or empty, use default greeting
      if (response.length === 0 || !response[0].text) {
        return [{
          id: `greeting_${Date.now()}`,
          type: 'text',
          text: "Hello! I'm Mira, your wellness guide. I'm here to listen and support you. How are you feeling today?",
          timestamp: new Date()
        }];
      }

      return response;
    } catch (error) {
      console.warn('Failed to get Botpress greeting, using fallback:', error);
      return [{
        id: `greeting_${Date.now()}`,
        type: 'text',
        text: "Hello! I'm Mira, your wellness guide. I'm here to listen and support you. How are you feeling today?",
        timestamp: new Date()
      }];
    }
  }

  // Check if service is connected
  isServiceConnected(): boolean {
    return this.isConnected;
  }

  // Get conversation ID
  getConversationId(): string | null {
    return this.conversationId;
  }
}

// Create a singleton instance
export const botpressService = new BotpressService({
  botId: 'mindbloom-therapist',
  userId: 'user_' + Date.now(),
  apiUrl: 'https://cdn.botpress.cloud/webchat/v3.2/shareable.html'
});

// Add global type declaration for window
declare global {
  interface Window {
    botpressWebChat?: any;
  }
}
