import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Shield, 
  Palette, 
  Bell, 
  Eye, 
  EyeOff,
  Heart,
  Phone,
  Lock,
  User,
  Sparkles,
  Moon,
  Sun,
  Settings,
  Check,
  AlertTriangle,
  Info,
  Save,
  RotateCcw,
  Star,
  Zap,
  Users,
  MessageCircle
} from 'lucide-react';

interface SettingsPageProps {
  user: any;
  onBack: () => void;
}

interface UserSettings {
  // Privacy Settings
  displayName: 'full' | 'nickname' | 'anonymous';
  showAvatar: boolean;
  showActivity: boolean;

  // Appearance Settings
  avatarStyle: 'pixel' | 'minimal' | 'detailed';
  theme: 'light' | 'dark' | 'auto';
  colorPalette: 'warm' | 'cool' | 'neutral' | 'vibrant';
  backgroundStyle: 'gradient' | 'solid' | 'pattern';

  // Notification Settings
  challengeReminders: boolean;
  storyNudges: boolean;
  wellnessCheckIns: boolean;
  communityUpdates: boolean;
  notificationFrequency: 'gentle' | 'moderate' | 'minimal';

  // Wellness Preferences
  wellnessFocus: string[];
  preferredChallenges: string[];
  moodTracking: boolean;
  goalSetting: boolean;

  // Emergency Settings
  emergencyContacts: string[];
  crisisMode: boolean;
}

const avatarStyles = [
  { id: 'pixel', name: 'Pixel Art', icon: 'ðŸŽ®', description: 'Retro gaming style' },
  { id: 'minimal', name: 'Minimal', icon: 'âšª', description: 'Clean and simple' },
  { id: 'detailed', name: 'Detailed', icon: 'ðŸ‘¤', description: 'More realistic' }
];

const colorPalettes = [
  { id: 'warm', name: 'Warm', colors: ['#FF6B6B', '#FFE66D', '#FF8E53'], description: 'Cozy and comforting' },
  { id: 'cool', name: 'Cool', colors: ['#4ECDC4', '#45B7D1', '#96CEB4'], description: 'Calm and peaceful' },
  { id: 'neutral', name: 'Neutral', colors: ['#A8A8A8', '#D3D3D3', '#F5F5F5'], description: 'Balanced and serene' },
  { id: 'vibrant', name: 'Vibrant', colors: ['#FF9FF3', '#54A0FF', '#5F27CD'], description: 'Energetic and uplifting' }
];

const wellnessFocusOptions = [
  { id: 'gratitude', name: 'Gratitude', icon: 'ðŸ™', description: 'Focus on appreciation and thankfulness' },
  { id: 'resilience', name: 'Resilience', icon: 'ðŸ’ª', description: 'Build strength and bounce back' },
  { id: 'calm', name: 'Calm', icon: 'ðŸ§˜', description: 'Find peace and mindfulness' },
  { id: 'connection', name: 'Connection', icon: 'ðŸ¤', description: 'Build relationships and community' },
  { id: 'creativity', name: 'Creativity', icon: 'ðŸŽ¨', description: 'Express yourself through art and ideas' },
  { id: 'growth', name: 'Growth', icon: 'ðŸŒ±', description: 'Learn and develop new skills' }
];

const emergencyContacts = [
  { name: 'National Suicide Prevention Lifeline', number: '988', available: '24/7' },
  { name: 'Crisis Text Line', number: 'Text HOME to 741741', available: '24/7' },
  { name: 'SAMHSA National Helpline', number: '1-800-662-4357', available: '24/7' }
];

export function SettingsPage({ user, onBack }: SettingsPageProps) {
  const [settings, setSettings] = useState<UserSettings>({
    displayName: 'anonymous',
    showAvatar: true,
    showActivity: false,
    avatarStyle: 'pixel',
    theme: 'auto',
    colorPalette: 'warm',
    backgroundStyle: 'gradient',
    challengeReminders: true,
    storyNudges: true,
    wellnessCheckIns: true,
    communityUpdates: false,
    notificationFrequency: 'gentle',
    wellnessFocus: ['gratitude', 'calm'],
    preferredChallenges: ['mindfulness', 'breathing'],
    moodTracking: true,
    goalSetting: true,
    emergencyContacts: [],
    crisisMode: false
  });

  const [activeSection, setActiveSection] = useState<string>('privacy');
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);

  const updateSetting = <K extends keyof UserSettings>(key: K, value: UserSettings[K]) => {
    setSettings(prev => ({ ...prev, [key]: value }));
    setHasChanges(true);
  };

  const handleSave = () => {
    // Save settings to localStorage or backend
    localStorage.setItem('mindbloom-settings', JSON.stringify(settings));
    setHasChanges(false);
    // Show success message
  };

  const handleReset = () => {
    setSettings({
      displayName: 'anonymous',
      showAvatar: true,
      showActivity: false,
      avatarStyle: 'pixel',
      theme: 'auto',
      colorPalette: 'warm',
      backgroundStyle: 'gradient',
      challengeReminders: true,
      storyNudges: true,
      wellnessCheckIns: true,
      communityUpdates: false,
      notificationFrequency: 'gentle',
      wellnessFocus: ['gratitude', 'calm'],
      preferredChallenges: ['mindfulness', 'breathing'],
      moodTracking: true,
      goalSetting: true,
      emergencyContacts: [],
      crisisMode: false
    });
    setHasChanges(true);
  };

  const handleEmergencyCall = (number: string) => {
    window.open(`tel:${number}`, '_self');
  };

  const sections = [
    { id: 'privacy', name: 'Privacy & Safety', icon: Shield, color: 'from-red-500 to-pink-500' },
    { id: 'appearance', name: 'Appearance', icon: Palette, color: 'from-purple-500 to-indigo-500' },
    { id: 'notifications', name: 'Notifications', icon: Bell, color: 'from-blue-500 to-cyan-500' },
    { id: 'wellness', name: 'Wellness Focus', icon: Heart, color: 'from-green-500 to-teal-500' },
    { id: 'emergency', name: 'Emergency', icon: Phone, color: 'from-orange-500 to-red-500' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200 p-4">
        <div className="max-w-6xl mx-auto flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button
              onClick={onBack}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              â†
            </button>
            <div>
              <h1 className="text-2xl font-bold text-gray-800 flex items-center">
                <Settings className="w-6 h-6 mr-2 text-blue-500" />
                Your Safe Space
              </h1>
              <p className="text-gray-600">Customize your wellness journey with complete privacy</p>
            </div>
          </div>
          {hasChanges && (
            <div className="flex items-center space-x-2">
              <button
                onClick={handleReset}
                className="px-3 py-1 text-gray-600 hover:bg-gray-100 rounded-lg transition-colors text-sm"
              >
                Reset
              </button>
              <button
                onClick={handleSave}
                className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors flex items-center space-x-2"
              >
                <Save className="w-4 h-4" />
                <span>Save Changes</span>
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="max-w-6xl mx-auto p-6">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Sidebar Navigation */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-2xl shadow-lg p-4 sticky top-6">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">Settings</h3>
              <nav className="space-y-2">
                {sections.map((section) => (
                  <button
                    key={section.id}
                    onClick={() => setActiveSection(section.id)}
                    className={`w-full flex items-center space-x-3 px-3 py-2 rounded-lg transition-all duration-200 ${
                      activeSection === section.id
                        ? `bg-gradient-to-r ${section.color} text-white shadow-lg`
                        : 'text-gray-600 hover:bg-gray-100'
                    }`}
                  >
                    <section.icon className="w-4 h-4" />
                    <span className="text-sm font-medium">{section.name}</span>
                  </button>
                ))}
              </nav>
            </div>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            <AnimatePresence mode="wait">
              {/* Privacy & Safety Section */}
              {activeSection === 'privacy' && (
                <motion.div
                  key="privacy"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  className="space-y-6"
                >
                  <div className="bg-white rounded-2xl shadow-lg p-6">
                    <div className="flex items-center space-x-3 mb-6">
                      <div className="w-10 h-10 bg-gradient-to-r from-red-500 to-pink-500 rounded-full flex items-center justify-center">
                        <Shield className="w-5 h-5 text-white" />
                      </div>
                      <div>
