import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { User, ChatMessage, AvatarState, CrisisDetection, WellnessSession } from '../types';
import { TTSService } from '../services/speechServiceSimple';

interface AppState {
  // User state
  user: User | null;
  isLoading: boolean;

  // Chat state
  messages: ChatMessage[];
  isTyping: boolean;

  // Avatar state
  avatarState: AvatarState;

  // Crisis detection
  crisisDetected: CrisisDetection | null;

  // Sessions
  sessions: WellnessSession[];

  // Speech service
  ttsService: TTSService;

  // Actions
  setUser: (user: User) => void;
  addMessage: (message: ChatMessage) => void;
  setTyping: (isTyping: boolean) => void;
  updateAvatarState: (state: Partial<AvatarState>) => void;
  setCrisisDetected: (crisis: CrisisDetection | null) => void;
  addSession: (session: WellnessSession) => void;
  initializeApp: () => Promise<void>;
  sendMessage: (content: string) => Promise<void>;
  processCrisisDetection: (message: string) => CrisisDetection | null;
  speakText: (text: string) => Promise<void>;
}

const CRISIS_KEYWORDS = {
  critical: ['suicide', 'kill myself', 'end it all', 'not worth living', 'want to die'],
  high: ['depressed', 'hopeless', 'worthless', 'can\'t go on', 'give up'],
  medium: ['sad', 'lonely', 'anxious', 'worried', 'stressed'],
  low: ['tired', 'overwhelmed', 'difficult', 'challenging']
};

export const useStore = create<AppState>()(
  persist(
    (set, get) => ({
      // Initial state
      user: null,
      isLoading: true,
      messages: [],
      isTyping: false,
      avatarState: {
        isVisible: false,
        isSpeaking: false,
        currentAnimation: 'idle',
        emotion: { type: 'neutral', intensity: 0.5 },
        position: { x: 0, y: 0, z: 0 }
      },
      crisisDetected: null,
      sessions: [],
      ttsService: new TTSService(),

      // Actions
      setUser: (user) => set({ user }),

      addMessage: (message) => set((state) => ({
        messages: [...state.messages, message]
      })),

      setTyping: (isTyping) => set({ isTyping }),

      updateAvatarState: (newState) => set((state) => ({
        avatarState: { ...state.avatarState, ...newState }
      })),

      setCrisisDetected: (crisis) => set({ crisisDetected: crisis }),

      addSession: (session) => set((state) => ({
        sessions: [...state.sessions, session]
      })),

      initializeApp: async () => {
        set({ isLoading: true });

        try {
          // Check for existing user in localStorage
          const savedUser = localStorage.getItem('mindbloom-user');
          if (savedUser) {
            const user = JSON.parse(savedUser);
            set({ user, isLoading: false });
          } else {
            set({ isLoading: false });
          }
        } catch (error) {
          console.error('Error initializing app:', error);
          set({ isLoading: false });
        }
      },

      sendMessage: async (content) => {
        const { user, addMessage, setTyping, processCrisisDetection, setCrisisDetected } = get();

        if (!user) return;

        // Add user message
        const userMessage: ChatMessage = {
          id: Date.now().toString(),
          userId: user.id,
          content,
          isFromUser: true,
          timestamp: new Date()
        };
        addMessage(userMessage);

        // Check for crisis keywords
        const crisis = processCrisisDetection(content);
        if (crisis) {
          setCrisisDetected(crisis);
        }

        setTyping(true);

        try {
          // Simulate AI response (replace with actual AI API call)
          await new Promise(resolve => setTimeout(resolve, 1500));

          const aiResponse = generateAIResponse(content, crisis);

          const aiMessage: ChatMessage = {
            id: (Date.now() + 1).toString(),
            userId: 'ai',
            content: aiResponse,
            isFromUser: false,
            timestamp: new Date(),
            emotion: { type: 'encouraging', intensity: 0.8 }
          };

          addMessage(aiMessage);

          // Update avatar to speak and use TTS
          get().updateAvatarState({
            isSpeaking: true,
            currentAnimation: 'speaking',
            emotion: { type: 'encouraging', intensity: 0.8 }
          });

          // Use TTS to speak the response
          try {
            await get().speakText(aiResponse);
          } catch (error) {
            console.warn('TTS failed:', error);
          }

          // Stop speaking after TTS completes
          get().updateAvatarState({
            isSpeaking: false,
            currentAnimation: 'idle'
          });

        } catch (error) {
          console.error('Error sending message:', error);
        } finally {
          setTyping(false);
        }
      },

      processCrisisDetection: (message) => {
        const lowerMessage = message.toLowerCase();

        for (const [severity, keywords] of Object.entries(CRISIS_KEYWORDS)) {
          for (const keyword of keywords) {
            if (lowerMessage.includes(keyword)) {
              return {
                keywords: [keyword],
                severity: severity as CrisisDetection['severity'],
                action: severity === 'critical' ? 'emergency' : 
                       severity === 'high' ? 'escalate' : 'monitor'
              };
            }
          }
        }

        return null;
      },

      speakText: async (text) => {
        const { ttsService } = get();
        try {
          await ttsService.speak(text, {
            rate: 0.9,
            pitch: 1.0,
            volume: 0.8
          });
        } catch (error) {
          console.error('TTS Error:', error);
          throw error;
        }
      }
    }),
    {
      name: 'mindbloom-storage',
      partialize: (state) => ({
        user: state.user,
        sessions: state.sessions
      })
    }
  )
);

// Helper function to generate AI responses
function generateAIResponse(userMessage: string, crisis: CrisisDetection | null): string {
  if (crisis) {
    switch (crisis.severity) {
      case 'critical':
        return "I'm really concerned about what you're sharing. Your safety is the most important thing right now. Please reach out to a crisis helpline immediately. In the US, you can call 988 for the Suicide & Crisis Lifeline. You're not alone, and there are people who want to help you.";
      case 'high':
        return "I can hear that you're going through a really difficult time right now. It takes courage to share these feelings. While I'm here to listen and support you, I want to encourage you to also reach out to a mental health professional or trusted person in your life. You deserve support.";
      case 'medium':
        return "I understand you're feeling overwhelmed right now. These feelings are valid, and it's okay to not be okay sometimes. Let's work together to find some strategies that might help you feel a bit better. Would you like to try a breathing exercise or talk about what's on your mind?";
      default:
        return "I hear you, and I'm here to support you through this. Sometimes life can feel really challenging, but you're taking a positive step by reaching out. Let's explore some ways we can work through this together.";
    }
  }

  // Generate empathetic responses based on common patterns
  const responses = [
    "I really appreciate you sharing that with me. It sounds like you're dealing with a lot right now.",
    "That sounds really challenging. I'm here to listen and support you through this.",
    "I can hear the strength in your words, even when things feel difficult. You're doing better than you might think.",
    "Thank you for trusting me with your thoughts. Let's work together to find some strategies that might help.",
    "I understand this is hard for you. Remember, it's okay to take things one step at a time.",
    "Your feelings are completely valid. Sometimes just talking about what's on our mind can help us feel a bit lighter."
  ];

  return responses[Math.floor(Math.random() * responses.length)];
}
