import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useStore } from './hooks/useStore';
import { AvatarScene } from './components/AvatarScene2D';
import { ChatInterface } from './components/ChatInterface';
import { UserAvatar } from './components/UserAvatar';
import { XPBar } from './components/XPBar';
import { WelcomeScreen } from './components/WelcomeScreen';
import { CrisisModal } from './components/CrisisModal';
import { LoadingScreen } from './components/LoadingScreen';
import { CommunityPlatform } from './components/CommunityPlatform';

function App() {
  const { 
    user, 
    isLoading, 
    crisisDetected,
    initializeApp,
    initializeBotpress,
    showInitialGreeting
  } = useStore();

  const [showWelcome, setShowWelcome] = useState(true);
  const [activeTab, setActiveTab] = useState<'chat' | 'community'>('chat');

  useEffect(() => {
    const initializeEverything = async () => {
      await initializeApp();
      await initializeBotpress();
    };
    initializeEverything();
  }, [initializeApp, initializeBotpress]);

  useEffect(() => {
    if (user) {
      setShowWelcome(false);
      // Show initial greeting after user is set and welcome screen is closed
      setTimeout(() => {
        showInitialGreeting();
      }, 1000);
    }
  }, [user, showInitialGreeting]);

  if (isLoading) {
    return <LoadingScreen />;
  }

  return (
    <Router>
      <div className="App min-h-screen bg-gradient-to-br from-primary-50 to-wellness-50">
        <AnimatePresence>
          {showWelcome && (
            <WelcomeScreen onComplete={() => setShowWelcome(false)} />
          )}
        </AnimatePresence>

        {crisisDetected && <CrisisModal />}

        <main className="relative h-screen overflow-hidden">
          {/* Header with XP Bar */}
          {user && (
            <motion.header 
              className="absolute top-0 left-0 right-0 z-10 p-4"
              initial={{ y: -100 }}
              animate={{ y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <div className="flex justify-between items-center">
                <div className="flex items-center space-x-4">
                  <UserAvatar level={user.avatarLevel} />
                  <div>
                    <h1 className="text-xl font-semibold text-gray-800">
                      Welcome, {user.nickname}
                    </h1>
                    <p className="text-sm text-gray-600">
                      Day {user.streak} streak
                    </p>
                  </div>
                </div>
                <XPBar currentXP={user.xp} level={user.avatarLevel} />
              </div>
            </motion.header>
          )}

          {/* Tab Navigation */}
          {user && (
            <div className="absolute top-20 left-0 right-0 z-10 p-4">
              <div className="flex space-x-1 bg-white/90 backdrop-blur-sm rounded-lg p-1 border border-gray-200 max-w-md mx-auto">
                <button
                  onClick={() => setActiveTab('chat')}
                  className={`flex-1 py-2 px-4 rounded-md transition-colors ${
                    activeTab === 'chat'
                      ? 'bg-primary-500 text-white'
                      : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  üí¨ Chat with Mira
                </button>
                <button
                  onClick={() => setActiveTab('community')}
                  className={`flex-1 py-2 px-4 rounded-md transition-colors ${
                    activeTab === 'community'
                      ? 'bg-emerald-500 text-white'
                      : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  üåç Community
                </button>
              </div>
            </div>
          )}

          {/* Main Content Area */}
          <div className="flex h-full pt-32">
            {activeTab === 'chat' ? (
              <>
                {/* Avatar Scene */}
                <div className="flex-1 relative">
                  <AvatarScene />
                </div>

                {/* Chat Interface */}
                <div className="w-96 bg-white/80 backdrop-blur-sm border-l border-gray-200">
                  <ChatInterface />
                </div>
              </>
            ) : (
              /* Community Platform */
              <div className="flex-1">
                <CommunityPlatform />
              </div>
            )}
          </div>
        </main>

        <Routes>
          <Route path="/breathing" element={<div>Breathing Exercise</div>} />
          <Route path="/journaling" element={<div>Journaling</div>} />
          <Route path="/exercises" element={<div>CBT Exercises</div>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
